$("#email").keyup(a=>{const b=a.target.value,c=$("#email-error");validateEmail(b)?c.hide():(c.show(),c.children().first().text("invalid Email"))});const validateEmail=a=>{return /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test((a+"").toLowerCase())};$("#password").keyup(a=>{const b=a.target.value,c=$("#password-error");6>b.length?(c.show(),c.children().first().text("Enter atleast 6 charecter")):c.hide()}),$("form").submit(a=>{const b=$("#email").val(),c=$("#password").val();(!validateEmail(b)||6>c.length)&&a.preventDefault()});