const likepressed=(a,b)=>{const c=$(b).parentsUntil(".row").last(),d=c.children("div").first().children().children();let e=parseInt(d.text());const f=c.children("div").last(),g=c.children("span");$.ajax({url:"/article/like/"+a,success:a=>{a.error?(g.text(a.message),g.show()):a.liked?(d.text(e+1),likeIcon(f,!0)):(d.text(e-1),likeIcon(f,!1))}})},likeIcon=(a,b)=>{b?(a.addClass("fas"),a.addClass("red-text"),a.removeClass("far")):(a.addClass("far"),a.removeClass("fas"),a.removeClass("red-text"))};